import React, {useState } from "react";
import TextField from "@material-ui/core/TextField";
import Container from "@material-ui/core/Container";
import { makeStyles } from "@material-ui/core/styles";
import { Button, Grid } from "@material-ui/core";
import Header from "./Header";
import CitizenDetail from './citizenDetail';
import EditCitizenDetails from './EditCitizenDetail'

import axios from'axios';

const useStyles = makeStyles((theme) => ({
  root: {
    marginTop: theme.spacing(8),
  },
  input: {
    [`& fieldset`]: {
      borderRadius: 18,
    },
  },
  button: {
    height: 50,
    marginTop: 16,
    marginLeft: 10,
  },
}));

export default function Search(props) {
console.log(props.history)
const [searchPtin, SetSearchPtin]= useState('')
const [userData , SetUserData] = useState([])
const [showData,SetShowData]= useState(false)

const handleChange = (event) => {
  SetSearchPtin(event.target.value );
};


const handleSubmit = (e)=>{
e.preventDefault();

console.log(searchPtin, "STATE")
axios.get(`http://localhost:4000/getthedata/${searchPtin}`)
.then(res=>{
  SetUserData(res.data[0])
  // console.log(res.data[0], "dataaaaaaaaa")
  SetShowData(true)
})
.catch(err=>{
  console.log(err, "errrrrrrrrrrrrrrrrrrr")
})
}

  const classes = useStyles();
  console.log(window.location.pathname,"Location")
  return (
    <React.Fragment>
      <Header />
      <Container className={classes.root} maxWidth="lg">
        <div className="serchBox">
        <form noValidate onSubmit={handleSubmit}>
          <TextField
            label="Search property by PTIN"
            margin="normal"
            variant="outlined"
            name="searchPtin"
            fullWidth
            className={classes.input}
            defaultValue={searchPtin}
            onChange={handleChange}
          />
         </form>
        </div>
      </Container>

  {showData ? <CitizenDetail userData={userData }/>:null} 
{  "/EditCitizenDetails" === window.location.pathname && <EditCitizenDetails updatedata={userData }/>
} 

    </React.Fragment>
  );
}
